tools_config.custom_tool_schema:
[
  {
    "name": "student_information_system_query",
    "description": "Query student information system for academic records and registration status",
    "input_schema": {
      "type": "object",
      "properties": {
        "record_type": {
          "type": "string",
          "enum": ["transcript_unofficial", "degree_audit", "holds_alerts", "registration_history", "academic_standing"]
        },
        "semester_scope": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["current", "previous", "fall_2024", "spring_2025", "summer_2025"]
          }
        },
        "include_transfer_credits": {
          "type": "boolean",
          "default": true
        }
      },
      "required": ["record_type"]
    }
  },
  {
    "name": "course_registration_system",
    "description": "Register for courses through university registration system with prerequisite validation",
    "input_schema": {
      "type": "object",
      "properties": {
        "course_reference_numbers": {
          "type": "array",
          "items": {
            "type": "string",
            "pattern": "^[0-9]{5}$"
          }
        },
        "registration_action": {
          "type": "string",
          "enum": ["add", "drop", "swap", "waitlist"]
        },
        "registration_term": {
          "type": "string",
          "enum": ["fall_2024", "spring_2025", "summer_2025_session1", "summer_2025_session2"]
        },
        "override_prerequisite": {
          "type": "boolean",
          "default": false
        },
        "grading_option": {
          "type": "string",
          "enum": ["letter_grade", "pass_fail", "audit", "credit_no_credit"]
        }
      },
      "required": ["course_reference_numbers", "registration_action", "registration_term"]
    }
  },
  {
    "name": "learning_management_submission",
    "description": "Submit coursework through integrated learning management system with plagiarism detection",
    "input_schema": {
      "type": "object",
      "properties": {
        "course_shell_id": {
          "type": "string",
          "pattern": "^[A-Z]{2,4}[0-9]{3,4}_[0-9]{3}_[0-9]{4}$"
        },
        "assignment_dropbox": {
          "type": "string",
          "enum": ["homework", "quiz", "midterm_exam", "final_project", "discussion_post", "peer_review"]
        },
        "submission_attempt": {
          "type": "integer",
          "minimum": 1,
          "maximum": 3
        },
        "file_types": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["pdf", "docx", "txt", "zip", "code_archive", "video_mp4"]
          }
        },
        "turnitin_consent": {
          "type": "boolean",
          "default": true
        },
        "late_submission_reason": {
          "type": "string",
          "enum": ["none", "technical_issue", "medical_emergency", "family_emergency", "accommodation"]
        }
      },
      "required": ["course_shell_id", "assignment_dropbox", "file_types"]
    }
  },
  {
    "name": "faculty_office_hours_booking",
    "description": "Book appointment slots during faculty office hours through academic scheduling system",
    "input_schema": {
      "type": "object",
      "properties": {
        "faculty_university_id": {
          "type": "string",
          "pattern": "^[a-z]{2,8}[0-9]{0,3}$"
        },
        "appointment_duration": {
          "type": "string",
          "enum": ["15_min", "30_min", "45_min", "60_min"]
        },
        "meeting_modality": {
          "type": "string",
          "enum": ["in_person_office", "zoom_meeting", "phone_call", "no_preference"]
        },
        "appointment_reason": {
          "type": "string",
          "enum": ["course_content", "assignment_clarification", "exam_review", "research_opportunity", "career_advice", "academic_planning"]
        },
        "preferred_time_slots": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["monday_morning", "monday_afternoon", "tuesday_morning", "tuesday_afternoon", "wednesday_morning", "wednesday_afternoon", "thursday_morning", "thursday_afternoon", "friday_morning", "friday_afternoon"]
          }
        }
      },
      "required": ["faculty_university_id", "appointment_reason"]
    }
  },
  {
    "name": "disability_services_coordination",
    "description": "Coordinate academic accommodations through disability services office with FERPA compliance",
    "input_schema": {
      "type": "object",
      "properties": {
        "accommodation_request": {
          "type": "string",
          "enum": ["exam_modifications", "note_taking_services", "assistive_technology", "housing_accommodation", "dining_accommodation"]
        },
        "affected_courses": {
          "type": "array",
          "items": {
            "type": "string",
            "pattern": "^[A-Z]{2,4}[0-9]{3,4}$"
          }
        },
        "documentation_status": {
          "type": "string",
          "enum": ["on_file", "pending_review", "requires_update", "initial_submission"]
        },
        "accommodation_timeline": {
          "type": "string",
          "enum": ["immediate", "next_exam", "next_semester", "permanent"]
        },
        "faculty_notification_consent": {
          "type": "boolean",
          "default": true
        }
      },
      "required": ["accommodation_request", "documentation_status"]
    }
  },
  {
    "name": "financial_aid_portal_access",
    "description": "Access financial aid information through secure student financial services portal",
    "input_schema": {
      "type": "object",
      "properties": {
        "aid_information_type": {
          "type": "string",
          "enum": ["award_letter", "loan_history", "work_study_eligibility", "scholarship_status", "payment_plan", "tax_documents"]
        },
        "academic_year": {
          "type": "string",
          "enum": ["2024-2025", "2023-2024", "2025-2026"]
        },
        "document_delivery": {
          "type": "string",
          "enum": ["online_portal", "secure_email", "physical_mail", "pickup_office"]
        },
        "dependency_override": {
          "type": "boolean",
          "default": false
        }
      },
      "required": ["aid_information_type", "academic_year"]
    }
  },
  {
    "name": "campus_resource_reservation",
    "description": "Reserve campus facilities and resources through centralized booking system",
    "input_schema": {
      "type": "object",
      "properties": {
        "facility_category": {
          "type": "string",
          "enum": ["study_room", "computer_lab", "presentation_space", "maker_space", "recording_studio", "collaborative_workspace"]
        },
        "building_preference": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["library_main", "student_union", "engineering_building", "business_school", "science_center", "arts_building"]
          }
        },
        "reservation_duration": {
          "type": "string",
          "enum": ["1_hour", "2_hours", "4_hours", "half_day", "full_day"]
        },
        "group_size": {
          "type": "integer",
          "minimum": 1,
          "maximum": 25
        },
        "equipment_needed": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["projector", "smart_board", "video_conference", "3d_printer", "recording_equipment", "presentation_clicker"]
          }
        },
        "recurring_reservation": {
          "type": "boolean",
          "default": false
        }
      },
      "required": ["facility_category", "reservation_duration", "group_size"]
    }
  },
  {
    "name": "academic_transcript_services",
    "description": "Request official transcripts through National Student Clearinghouse or registrar services",
    "input_schema": {
      "type": "object",
      "properties": {
        "transcript_type": {
          "type": "string",
          "enum": ["official_sealed", "electronic_official", "student_copy", "degree_verification", "enrollment_verification"]
        },
        "recipient_organization": {
          "type": "string",
          "enum": ["graduate_school", "employer", "scholarship_foundation", "government_agency", "self"]
        },
        "delivery_method": {
          "type": "string",
          "enum": ["electronic_secure", "postal_mail", "pickup_registrar", "third_party_service"]
        },
        "processing_priority": {
          "type": "string",
          "enum": ["standard", "rush_24_hours", "emergency_same_day"]
        },
        "include_in_progress": {
          "type": "boolean",
          "default": false
        },
        "ferpa_release_signed": {
          "type": "boolean",
          "default": true
        }
      },
      "required": ["transcript_type", "recipient_organization", "delivery_method"]
    }
  },
  {
    "name": "student_conduct_case_tracking",
    "description": "Track academic integrity and student conduct cases through judicial affairs system",
    "input_schema": {
      "type": "object",
      "properties": {
        "case_inquiry_type": {
          "type": "string",
          "enum": ["case_status", "hearing_schedule", "resolution_summary", "appeal_deadline", "disciplinary_record"]
        },
        "case_category": {
          "type": "string",
          "enum": ["academic_integrity", "code_of_conduct", "housing_policy", "parking_violation", "library_fines"]
        },
        "representation_needed": {
          "type": "boolean",
          "default": false
        },
        "privacy_notification": {
          "type": "string",
          "enum": ["student_only", "emergency_contact", "parent_guardian", "no_notification"]
        }
      },
      "required": ["case_inquiry_type"]
    }
  }
]
